import mongoose from "mongoose";

const PostSchema=mongoose.Schema({
    title:{
        type:String,
        required:false,
        unique:true
    },
    username:{
        type:String,
        required:true,
    },
    picture:{
        type:String,
        required:true,
    },
    preview:{
        type:Number,
        required:true,
    },
    createdAt:{
        type:Date,
    },

});
const post=mongoose.model('imagePost',PostSchema);
export default post;